/* DistStatDAO.selectMonTemp */ SELECT TRN, SUM(DECODE(SMON,'01',ACNT,'')) AS M01, SUM(DECODE(SMON,'02',ACNT,'')) AS M02, SUM(DECODE(SMON,'03',ACNT,'')) AS M03, SUM(DECODE(SMON,'04',ACNT,'')) AS M04, SUM(DECODE(SMON,'05',ACNT,'')) AS M05, SUM(DECODE(SMON,'06',ACNT,'')) AS M06, SUM(DECODE(SMON,'07',ACNT,'')) AS M07, SUM(DECODE(SMON,'08',ACNT,'')) AS M08, SUM(DECODE(SMON,'09',ACNT,'')) AS M09, SUM(DECODE(SMON,'10',ACNT,'')) AS M10, SUM(DECODE(SMON,'11',ACNT,'')) AS M11, SUM(DECODE(SMON,'12',ACNT,'')) AS M12, SUM(ACNT) AS TOT FROM ( SELECT TMP_RACE_NUMBER AS TRN, SUBSTR(STND_MON,1,4) AS SYEAR, SUBSTR(STND_MON,5,2) AS SMON, AUTO_DRIVING_DIST AS ACNT FROM TB_ST_HT_TEMP_AUTO_MON WHERE 1=1
AND TMP_RACE_NUMBER LIKE '%'||#sKeyword#||'%'
AND STND_MON LIKE #sDate#||'%' ) X GROUP BY TRN ORDER BY TRN

/* DistStatDAO.selectMonUser */ SELECT TRN, SUM(DECODE(SMON,'01',ACNT,'')) AS M01, SUM(DECODE(SMON,'02',ACNT,'')) AS M02, SUM(DECODE(SMON,'03',ACNT,'')) AS M03, SUM(DECODE(SMON,'04',ACNT,'')) AS M04, SUM(DECODE(SMON,'05',ACNT,'')) AS M05, SUM(DECODE(SMON,'06',ACNT,'')) AS M06, SUM(DECODE(SMON,'07',ACNT,'')) AS M07, SUM(DECODE(SMON,'08',ACNT,'')) AS M08, SUM(DECODE(SMON,'09',ACNT,'')) AS M09, SUM(DECODE(SMON,'10',ACNT,'')) AS M10, SUM(DECODE(SMON,'11',ACNT,'')) AS M11, SUM(DECODE(SMON,'12',ACNT,'')) AS M12, SUM(ACNT) AS TOT FROM ( SELECT FN_CT_GET_AGENCY_NAME(USER_ID) AS TRN, SUBSTR(STND_MON,1,4) AS SYEAR, SUBSTR(STND_MON,5,2) AS SMON, AUTO_DRIVING_DIST AS ACNT FROM TB_ST_HT_USER_AUTO_MON WHERE 1=1
AND FN_CT_GET_AGENCY_NAME(USER_ID) LIKE '%'||#sKeyword#||'%'
AND STND_MON LIKE #sDate#||'%' ) X GROUP BY TRN ORDER BY TRN

/* DistStatDAO.selectMonTempChart */ SELECT TMP_RACE_NUMBER AS TRN, TO_CHAR(TO_DATE(STND_MON,'YYYYMM'),'MM')||'월' AS STERM, AUTO_DRIVING_DIST AS ACNT FROM TB_ST_HT_TEMP_AUTO_MON WHERE 1=1
AND TMP_RACE_NUMBER LIKE '%'||#sKeyword#||'%'
AND STND_MON LIKE #sDate#||'%' ORDER BY STERM, TRN

/* DistStatDAO.selectMonUserChart */ SELECT FN_CT_GET_AGENCY_NAME(USER_ID) AS TRN, TO_CHAR(TO_DATE(STND_MON,'YYYYMM'),'MM')||'월' AS STERM, AUTO_DRIVING_DIST AS ACNT FROM TB_ST_HT_USER_AUTO_MON WHERE 1=1
AND FN_CT_GET_AGENCY_NAME(USER_ID) LIKE '%'||#sKeyword#||'%'
AND STND_MON LIKE #sDate#||'%' ORDER BY STERM, TRN

/* DistStatDAO.selectYearAll */ WITH PV AS ( SELECT '전체' AS TRN, NVL(STND_YEAR,-1) AS SYEAR, SUM(AUTO_DRIVING_DIST) AS ACNT FROM TB_ST_HT_TEMP_AUTO_YEAR WHERE 1=1 AND STND_YEAR BETWEEN #sDate# AND #eDate# GROUP BY CUBE(STND_YEAR) ) SELECT * FROM PV PIVOT (SUM(ACNT) FOR SYEAR IN
$yearList[]$
) WHERE TRN IS NOT NULL ORDER BY TRN

/* DistStatDAO.selectYearTemp */ WITH PV AS ( SELECT TMP_RACE_NUMBER AS TRN, NVL(STND_YEAR,-1) AS SYEAR, SUM(AUTO_DRIVING_DIST) AS ACNT FROM TB_ST_HT_TEMP_AUTO_YEAR WHERE 1=1
AND TMP_RACE_NUMBER LIKE '%'||#sKeyword#||'%'
AND STND_YEAR BETWEEN #sDate# AND #eDate# GROUP BY CUBE(TMP_RACE_NUMBER, STND_YEAR) ) SELECT * FROM PV PIVOT (SUM(ACNT) FOR SYEAR IN
$yearList[]$
) WHERE TRN IS NOT NULL ORDER BY TRN

/* DistStatDAO.selectYearUser */ WITH PV AS ( SELECT FN_CT_GET_AGENCY_NAME(USER_ID) AS TRN, NVL(STND_YEAR,-1) AS SYEAR, SUM(AUTO_DRIVING_DIST) AS ACNT FROM TB_ST_HT_USER_AUTO_YEAR WHERE 1=1
AND FN_CT_GET_AGENCY_NAME(USER_ID) LIKE '%'||#sKeyword#||'%'
AND STND_YEAR BETWEEN #sDate# AND #eDate# GROUP BY CUBE(FN_CT_GET_AGENCY_NAME(USER_ID), STND_YEAR) ) SELECT * FROM PV PIVOT (SUM(ACNT) FOR SYEAR IN
$yearList[]$
) WHERE TRN IS NOT NULL ORDER BY TRN

/* DistStatDAO.selectYearTempChart */ SELECT TMP_RACE_NUMBER AS TRN, STND_YEAR||'년' AS STERM, AUTO_DRIVING_DIST AS ACNT FROM TB_ST_HT_TEMP_AUTO_YEAR WHERE 1=1
AND TMP_RACE_NUMBER LIKE '%'||#sKeyword#||'%'
